import React, { useState, useRef, useEffect } from 'react';
import { MapContainer, TileLayer, Rectangle, useMap } from 'react-leaflet';
import L from 'leaflet';
import './App.css';

// Custom hook to get map instance
function MapController({ bounds, setBounds }) {
  const map = useMap();

  useEffect(() => {
    if (map) {
      map.on('boxzoomend', (e) => {
        setBounds(e.boxZoomBounds);
      });
    }
  }, [map, setBounds]);

  return null;
}

function App() {
  const [bounds, setBounds] = useState(null);
  const [format, setFormat] = useState('png');
  const [zoom, setZoom] = useState(15);
  const mapRef = useRef();

  const handleDownload = () => {
    if (!bounds) {
      alert('Please select an area on the map first.');
      return;
    }

    // Placeholder for download logic
    alert(`Downloading ${format} at zoom ${zoom} for bounds: ${bounds.toBBoxString()}`);
    console.log('Bounds:', bounds.toBBoxString());
    // Here you would integrate a library like leaflet-image or implement tile stitching
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>OSM Map Exporter</h1>
      </header>
      <div className="map-container">
        <MapContainer
          center={[51.505, -0.09]}
          zoom={zoom}
          scrollWheelZoom={true}
          style={{ height: '600px', width: '100%' }}
          whenCreated={mapInstance => { mapRef.current = mapInstance; }}
        >
          <TileLayer
            attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          />
          {bounds && (
            <Rectangle bounds={bounds} pathOptions={{ color: 'red', weight: 1, fillOpacity: 0.1 }} />
          )}
          <MapController bounds={bounds} setBounds={setBounds} />
        </MapContainer>
      </div>
      <div className="controls">
        <div>
          <label>Format:</label>
          <select value={format} onChange={(e) => setFormat(e.target.value)}>
            <option value="png">PNG</option>
            <option value="svg">SVG</option>
          </select>
        </div>
        <div>
          <label>Zoom Level:</label>
          <input
            type="number"
            value={zoom}
            onChange={(e) => setZoom(parseInt(e.target.value))}
            min="1"
            max="19"
          />
        </div>
        <button onClick={handleDownload}>Download Map</button>
      </div>
    </div>
  );
}

export default App;
